import React, { useState, useContext } from 'react';
import { Redirect } from 'react-router-dom'
import {AuthContext } from './AuthContext'

export function LoginPage( props) {

    const [submitted, setSubmitted] = useState(false);
    const [loading, setLoading] = useState(false);
    const [disable, setDisable] = useState(false);
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const userContext = useContext(AuthContext);

    const handleSubmit = (e) => {
        e.preventDefault();
        setSubmitted(true)

        // stop here if form is invalid
        if (!(username && password)) {
            return;
        }

        setLoading(true)
        userContext.login(username, password);
            /*.then(
                user => {
                    const { from } = this.props.location.state || { from: { pathname: "/" } };
                    this.props.history.push(from);
                },
                error => this.setState({ error, loading: false })
            );*/
    }

    if (userContext.isAuth) {
        return (
            <Redirect to={props.location.state.from} />
        )
    } else {
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login </h2>
                <form name="form" onSubmit={ handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" onChange={setUsername} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" onChange={setPassword} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" >Login</button>
                        {loading &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
        )
    }
}